import { createContext, useState } from "react";

import api from "../services/api";

export const AuthContext = createContext({});

export const UserContext = ({ children }) => {
    const [client, setClient] = useState();
    const [userNow, setUserNow] = useState();
    const [listaPedidos, setListaPedidos] = useState([]);

    const register = (userName, userEmail, userPassword, userContact, userCep, userLogradouro, userNumero, userComplemento, userBairro, userCity, userUf) => {
        
        api.post('/clients', {
            name: userName,
            email: userEmail,
            password: userPassword,
            contact: userContact,
            cep: userCep,
            logradouro: userLogradouro,
            number: userNumero,
            complemento: userComplemento,
            bairro: userBairro,
            city: userCity,
            uf: userUf
        }).then((response) => {
            console.log(response.data)
        }).catch((error) => {
            console.error(error.response)
        })

    }


    const login = (userEmail = {}, userPassword) => {  
        api.get('/clients')
        .then(({ data }) => {
            data.map((item) => {    
                if(item.email === userEmail && item.password === userPassword){
                    setClient({ userEmail, userPassword })
                    console.log(userEmail, userPassword)
                    setUserNow(userEmail)
                } else {
                    return "Usuário não encontrado."
                }
            })
        })
    }

    const logout = () => {
        setClient(null);
    }


    const pedidosListados = (sabor, qntPizzas) => {
        if(listaPedidos.length === 0){
            listaPedidos.push({sabor,qntPizzas})
            console.log(listaPedidos)

        } else if(listaPedidos.length > 0){
            listaPedidos.push({sabor,qntPizzas})
            console.log(listaPedidos)
            
        }
        
    }
    
    return(
        <AuthContext.Provider 
        value={{
            client, 
            userNow, 
            // loginOK: !!client, 
            login, 
            register, 
            logout,
            listaPedidos,
            pedidosListados
        }}
            >
                {children}
        </AuthContext.Provider>
    )

}
