import React, { useEffect, useState } from "react";

// Componentes da biblioteca react bootstrap
import Container  from "react-bootstrap/Container";
import  Form  from "react-bootstrap/Form";
import Col from 'react-bootstrap/Col'
import Row  from "react-bootstrap/Row";

// Componentes da própria aplicação
import ButtonEntrar from "../../components/buttons/buttonEntrar";
import logoPizzaria from '../../assets/pizzaria_giuseppe.png'

// Componentes de outras bibliotecas
import {FaArrowAltCircleLeft} from "react-icons/fa";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

// Importando o CSS
import './registerAdressStyle.css'

import useAuth from "../../hooks/useAuth";

export default function RegisterUserAdress(){
    let navigate = useNavigate();
    const { registerAdress } = useAuth();

    const[adress, setAdress] = useState([])
    const[cep, setCep] = useState('');

    const [logradouro, setLogradrouro] = useState('');
    const [numero, setNumero] = useState('');
    const [complem, setComplem] = useState('');
    const [bairro, setBairro] = useState('');
    const [cidade, setCidade] = useState('');
    const [uf, setUF] = useState('');

    const [error, setError] = useState();

    useEffect(() => {
        axios.get(`https://viacep.com.br/ws/${cep}/json/`).then(setAdress).then((data)=>{
            if(cep.length === 8 || cep.length === 9){
                setLogradrouro(adress.data.logradouro);
                setNumero(adress.data.numero);
                setComplem(adress.data.complemento);
                setBairro(adress.data.bairro);
                setCidade(adress.data.localidade);
                setUF(adress.data.uf);
            } else{
                console.log("CEP incompleto.")
            }
        });
    })

    const handleRegisterAdress = () => {
        if(!cep, !logradouro, !numero, !bairro, !cidade, !uf){
            setError("Preencha todos os campos!!")
            return
        } 

        const response = registerAdress({cep, logradouro, numero, complem, bairro, cidade, uf})

        if(!response){
            console.log("Nada bom!")
            return;
        }

        alert("Endereço cadastrado!")
        navigate("/localPedidos")
    }

    
        
    return(
        <Container fluid className="containerMainRegisterAdress">
            <Row className="containerHeaderRegisterAdress">
                <Col>
                    <Link to="/registerUser" >
                        <FaArrowAltCircleLeft size="20px" color="#D30000"/>
                    </Link>
                </Col>
                <Col className="containerLogoRegisterAdress">
                    <Link to="/">
                        <img
                            className="d-block w-20"
                            src={logoPizzaria}
                            alt="Pizza Calabresa"
                            height="50px"
                        />
                    </Link>
                </Col>
            </Row>
            <Form className="containerFormRegisterAdress">
                <Row>
                <h1 style={{color: "#D30000", fontWeight:"bold"}}>
                        Cadastro de Cliente
                    </h1>
                    <p>Continue seu cadastro preenchendo seu endereço</p>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>CEP</Form.Label>
                    <Form.Control 
                        type="text" 
                        placeholder="CEP" 
                        style={{backgroundColor:"#D8D8D8", width:"120px", textAlign:"center"}}
                        onChange={e => setCep(e.target.value)}
                        maxLength={9}
                    />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Logradouro</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Logradouro" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={logradouro}

                        />
                    </Col>
                    <Col>
                        <Form.Label>Número</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Número" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={numero}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Complemento</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Complemento" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={complem}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Bairro</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Bairro" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={bairro}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Cidade</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Cidade" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={cidade}
                        />
                    </Col>
                    <Col>
                        <Form.Label>UF</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="UF" 
                            maxLength={2}
                            style={{backgroundColor:"#D8D8D8", width:"50px", textAlign:"center"}} 
                            value={uf}
                        />
                    </Col>
                </Row>
                <Row>
                    <p></p>
                    <Col style={{display:"flex", justifyContent:"flex-end"}}>
                        <ButtonEntrar 
                            name="Pronto!" 
                            action={handleRegisterAdress}
                        />
                    </Col>
                </Row>
            </Form>
        </Container>
    )
}
