import React, {useState, useEffect} from "react";

// Componentes da biblioteca react bootstrap
import Container  from "react-bootstrap/Container";
import  Form  from "react-bootstrap/Form";
import Col from 'react-bootstrap/Col'
import Row  from "react-bootstrap/Row";

// Componentes da própria aplicação
import ButtonEntrar from "../../components/buttons/buttonEntrar";
import logoPizzaria from '../../assets/pizzaria_giuseppe.png'

// Componentes de outras bibliotecas
import {FaArrowAltCircleLeft} from "react-icons/fa";
import { Link } from "react-router-dom";

// Importando o CSS
import './loginUserStyle.css'

import api from "../../services/api";

export default function LoginUser(){
    const [userEmail, setUserEmail] = useState();
    const [userPassword, setUserPassword] = useState();

    const login = () => {
        api.get('/pizzas')
        .then(({data}) => {
            if(data.email === userEmail && data.password === userPassword){
                console.log("OK")
            }
        })
    }

    useEffect(() => {
        api.get('/pizzas')
        .then(({data}) => {
            if(data.email === userEmail && data.password === userPassword){
                console.log("OK")
            }
        })
    })
    

    return(
        <Container fluid className="containerMainLogin">
            <Row className="containerHeaderLogin">
                <Col>
                    <Link to="/">
                        <FaArrowAltCircleLeft size="20px" color="#D30000"/>
                    </Link>
                </Col>
                <Col className="containerLogoHeaderLogin">
                    <Link to="/">
                        <img
                            className="d-block w-20"
                            src={logoPizzaria}
                            alt="Pizza Calabresa"
                            height="50px"
                        />
                    </Link>
                </Col>
            </Row>
            <Row style={{marginLeft: "1%"}}>
                <h1 style={{fontWeight:"bold"}}>
                    Portal do Cliente
                </h1>
                <p>Efetue seu login para fazer seu pedido.</p>
            </Row>
            <Row className="containerInputsLogin">
                <Col>
                    <Form>
                        <Form.Group>
                            <Form.Label>Seu Email</Form.Label>
                            <Form.Control 
                                placeholder="Email" 
                                style={{backgroundColor:"#D8D8D8"}}
                                onChange={(e) => {
                                    setUserEmail(e.target.value)
                                }}
                            />

                            <Form.Label>Sua senha</Form.Label>
                            <Form.Control 
                                placeholder="Sua senha" 
                                style={{backgroundColor:"#D8D8D8"}}
                                onChange={(e) => {
                                    setUserPassword(e.target.value)
                                }}
                            />
                        </Form.Group>
                    </Form>
                    <ButtonEntrar name="Entrar"/>

                    <div className="containerNovoClienteLogin">
                        <p>Cliente novo?</p>
                        <Link to="/registerUser"id="linkLogin">
                            <p>Faça seu cadastro.</p>
                        </Link>
                    </div>

                </Col>
                <Col className="containerRightImageLogin">
                    <img
                        className="d-block w-400"
                        src={logoPizzaria}
                        alt="Pizza Calabresa"
                        height="400px"
                    />
                </Col>
            </Row>
        </Container>
    )
}
