import React, {useState, useEffect} from "react";
import { Dropdown } from "react-bootstrap";

import {Modal, Row, Form, Button, Badge} from 'react-bootstrap'

import { FaPlusCircle } from "react-icons/fa";
import { FaArrowCircleRight } from "react-icons/fa";

import api from "../../services/api";

import './modalStyle.css'

export default function ModalPizzas(){
    const [show, setShow] = useState(false);
    const [saboresList, setSaboresList] = useState([])
    const [saboresCliente, setSaboresCliente] = useState([])
    const [checkState, setCheckState] = useState(false)

    const handleClose = () => setShow(false)
    const handleShow = () => setShow(true)

    useEffect(() => {
        api.get('/pizzas')
        .then(({ data }) => {
            setSaboresList(data)
        })
    })

    const handleOnChange = () => {
        if(checkState === false) {
            setCheckState(true)
        } e
        
    }

    const selecionarSabor = (atual) => {
        saboresCliente.push(atual)
    }

    


    return(
        <>
            <Button onClick={handleShow} bsPrefix="botaoModalPedir" style={{width:"175px"}}>
                Peça uma pizza 
                <FaPlusCircle color="#fff"/>
            </Button>
        <Modal show={show} onHide={handleClose} backdrop="static" style={{fontWeight:"bold"}}>
            <Modal.Header closeButton>
                <Modal.Title>Escolha o sabor da pizza:</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Row>
                    <Form>
                        <p>Quantidade de saboresList na pizza:</p>
                        <div className="containerCheckbox">
                            <Form.Check 
                                label="1"
                                value={true}
                                checked={checkState}
                                onChange={() => handleOnChange()}
                            />
                            <Form.Check 
                                label="2"
                            />
                            <Form.Check 
                                label="3"
                            />
                        </div>
                    </Form>
                </Row>
                <Row>
                    <Dropdown drop="end" >
                        <Dropdown.Toggle style={{backgroundColor:"#D30000", border:"0", fontWeight: "bold"}}>
                            Escolha o sabor
                        </Dropdown.Toggle >
                        <Dropdown.Menu>
                            {saboresList.map((item) => {
                                const {sabor} = item

                                return(
                                    <Dropdown.Item onClick={() => {selecionarSabor(sabor)}}>
                                        {sabor}
                                    </Dropdown.Item>
                                )
                            })}
                            
                        </Dropdown.Menu>
                    </Dropdown>
                </Row>
                <Row className="rowSaborSelecionado">
                    {saboresCliente.map((item) => {
                        
                        return(
                            <Badge id="sabor" bg="#D30000">
                                {item}
                            </Badge>
                        )
                    })}
                    
                </Row>
    
                <Row>
                    <Form>
                        <p>Deseja borda recheada?</p>
                        <div className="containerCheckbox">
                            <Form.Check label="Sim"/>
                            <Form.Check label="Não"/>
                        </div>
                    </Form>
                </Row>
                <Row>
                <Dropdown drop="end" >
                        <Dropdown.Toggle style={{backgroundColor:"#D30000", border:"0", fontWeight: "bold"}}>
                            Escolha o sabor
                        </Dropdown.Toggle >
                        <Dropdown.Menu>
                            <Dropdown.Item>Nutella</Dropdown.Item>
                            <Dropdown.Item>Catupry</Dropdown.Item>
                            <Dropdown.Item>Cheddar</Dropdown.Item>
                            <Dropdown.Item>Parmesão</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Row>
                <Row className="rowSaborSelecionado">
                    {/* <Badge id="sabor" bg="#D30000">
                        Calabresa
                    </Badge>
                    <Badge id="sabor" bg="#D30000">
                        Calabresa
                    </Badge>
                    <Badge id="sabor" bg="#D30000">
                        Calabresa
                    </Badge> */}
                </Row>

                <Row style={{justifyContent:"end"}}>
                    <Button bsPrefix="botaoAvancaModal" style={{width:"150px"}}>
                        Avançar
                        <FaArrowCircleRight  size={20}/>
                    </Button>
                </Row>
            </Modal.Body>
        </Modal>
        </>
)
}
