import { createContext, useEffect, useState } from "react";

import api from "../services/api";

export const AuthContext = createContext({});

export const AuthProvider = ({ children }) => {
    const [client, setClient] = useState()

    const register = (userName, userEmail, userPassword, userContact) => {
        const verifica = api.get('/clients').then(({data}) => {data.filter((data) => data.email === userEmail)})

        if (verifica?.length){
            return "Conta já existente"
        } else{
            
        }

        

    }

    const login = (userEmail, userPassword) => {  
        api.get('/clients')
        .then(({ data }) => {
            data.map((item) => {    
                if(item.email === userEmail && item.password === userPassword){
                    setClient({ userEmail, userPassword })
                }
            })
        })
    }

    const logout = () => {
        setClient(null);
    }
    
    return(
        <AuthContext.Provider value={{client, loginOK: !!client, login, register, logout}}>
            {children}
        </AuthContext.Provider>
    )

}
