import React, {createContext, useState} from "react";

export const AuthContextPedido = createContext({});

export const UserProvider = ({ children }) => {
    const [listaPedidos] = useState([]);

    const pedidosListados = (sabor, qntPizzas) => {
        if(listaPedidos.length === 0){
            listaPedidos.push({sabor,qntPizzas})
            console.log(listaPedidos)

        } else if(listaPedidos.length > 0){
            listaPedidos.push({sabor,qntPizzas})
            console.log(listaPedidos)
        }
        
    }

    return (
        <AuthContextPedido.Provider value={listaPedidos}>
            {children}
        </AuthContextPedido.Provider>
    )
}
