import React, { useEffect, useState } from "react";
import CardPizza from "../cards/cardCardapio";
import Container  from "react-bootstrap/Container";
import Row  from "react-bootstrap/Row";
import Col from "react-bootstrap/Col"

import pizzaCalabresa from '../../assets/pizzaCalabresa.jpg';
import pizzaQueijo from '../../assets/pizzaQueijo.jpg';
import pizzaDoce from '../../assets/pizzaBanana.jpg';

import api from "../../services/api";

import './cardapioStyle.css'



export default function Recipe(){
   // const [pizzas, setPizzas ] = useState({sabor: '', preco:'', image:''});
   
   const [pizzas, setPizzas ] = useState([]);
   // const {sabor,  preco, image} = pizzas

   // const getPizzas = async () => {
   //    try{
   //       const response = await api.get();
   //       setPizzas(response.data)
   //    } catch(error){
   //       console.error(error)
   //    }
      
   // }

   // useEffect(() => {
   //    getPizzas();
   //    console.log(pizzas)
   // }, []);

   useEffect(() =>{
      api.get('/pizzas')
      .then(({data}) => {
         setPizzas(data)
         console.log(pizzas[0].sabor)
      })
   })


//    useEffect(() =>{
//       api.get('/pizzas').then((response) => response.json()).then(setPizzas).then(console.log(pizzas)) 
//   }, [])


    return(
        <Container fluid className="containerCardapio">
            
            <Row style={{alignItems:"center", justifyContent:"center", paddingTop:"20px"}}>
               {pizzas.map((item) => {
                  const {sabor, preco, image} = item
                  return(
                     <CardPizza 
                        image={image}
                        pizza={sabor}
                        preco={preco}
                     />
                  )
               })}
                  
                     
            </Row>
        </Container>
        
    )
}
