import React, {useState, useEffect} from "react";

// Componentes da biblioteca react bootstrap
import {Container, Form, Col, Row} from 'react-bootstrap'

// Componentes da própria aplicação
import logoPizzaria from '../../assets/pizzaria_giuseppe.png'
import ButtonEntrar from "../../components/buttons/buttonEntrar";

// Componentes de outras bibliotecas
import {FaArrowAltCircleLeft, FaRegClipboard} from "react-icons/fa";
import { Link, useNavigate } from "react-router-dom";

// Importando o CSS
import "./registerUserStyle.css"

import useAuth from "../../hooks/useAuth";

import axios from "axios";

export default function RegisterUser(){
    let navigate = useNavigate();
    const { register } = useAuth();
    const [error, setError] = useState();

    // Váriaveis das informações básicas de Cliente
    const [userName, setUserName] = useState();
    const [userEmail, setUserEmail] = useState();
    const [userContact, setUserContact] = useState();
    const [userPassword, setUserPassword] = useState();
    const [passwordConfirm, setPasswordConfirm] = useState()
    

    // Váriaveis das informações de endereço de Cliente
    const [adress, setAdress] = useState([])
    const [userCep, setCep] = useState('');
    const [celogradouro, setLogradrouro] = useState('');
    const [numero, setNumero] = useState('');
    const [complem, setComplem] = useState('');
    const [bairro, setBairro] = useState('');
    const [cidade, setCidade] = useState('');
    const [uf, setUF] = useState('');

    useEffect(() => {
        axios.get(`https://viacep.com.br/ws/${UserCep}/json/`).then(setAdress).then(()=>{
            if(UserCep.length === 8 || UserCep.length === 9){
                setLogradrouro(adress.data.logradouro);
                setNumero(adress.data.numero);
                setComplem(adress.data.complemento);
                setBairro(adress.data.bairro);
                setCidade(adress.data.localidade);
                setUF(adress.data.uf);
            } else{
                console.log("CEP incompleto.")
            }
        })
    })

    const handleRegister = () => {
        if (!userEmail | !userPassword | !userContact | !userName | !passwordConfirm){
            setError("Preencha todos os campos!!")
            return
        } else if (userPassword !== passwordConfirm){
            setError("Senhas Diferentes")
            return
        }
        
        console.log(adress.data, UserCep)

        const response = register(
            userName, 
            userEmail, 
            userPassword, 
            userContact,
            userCep, 
            userLogradouro,
            userNumero,
            complem,
            bairro,
            cidade,
            uf
            );

        if(response) {
            console.log("Nada bom")
            return;
        } 
        
        alert("Usuário cadastrado!")
        // navigate("/loginUser")

    }

    


    return(
        <>
        <Container fluid className="containerMainRegister">
            <Row className="containerHeaderRegister">
                <Col>
                    <Link to="/loginUser" >
                        <FaArrowAltCircleLeft size="20px" color="#D30000"/>
                    </Link>
                </Col>
                <Col className="containerLogoRegister">
                    <Link to="/">
                        <img
                            className="d-block w-20"
                            src={logoPizzaria}
                            alt="Pizza Calabresa"
                            height="50px"
                        />
                    </Link>
                </Col>
            </Row>
            <Form className="containerFormRegister">
                <Row style={{color: "#D30000", textAlign:'center'}}>
                    <h1>
                        Cadastro de Cliente
                    </h1>
                    <p>Comece seu cadastro com informações básicas</p>
                    <FaRegClipboard size={50}/>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Nome</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Nome completo" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                setUserName(e.target.value)
                            }}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Número para contato</Form.Label>
                        <Form.Control 
                            type="tel" 
                            placeholder="(xx) xxxxx-xxxx" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) =>{ 
                                setUserContact(e.target.value)
                            }}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Email</Form.Label>
                        <Form.Control 
                       
                            type="email" 
                            placeholder="Email" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                setUserEmail(e.target.value)
                            }}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Senha</Form.Label>
                        <Form.Control 
                            type="password" 
                            placeholder="Senha" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                setUserPassword(e.target.value)
                            }}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Confirmar Senha</Form.Label>
                        <Form.Control 
                            type="password" 
                            placeholder="Confirmar Senha" 
                            style={{backgroundColor:"#D8D8D8"}}
                        onChange={(e) => {
                            setPasswordConfirm(e.target.value)
                        }}
                        />
                    </Col>
                </Row>
            </Form>

            <Form className="containerFormRegisterAdress">
                <Row>
                    <Col>
                        <Form.Label>CEP</Form.Label>
                    <Form.Control 
                        type="text" 
                        placeholder="CEP" 
                        style={{backgroundColor:"#D8D8D8", width:"120px", textAlign:"center"}}
                        onChange={e => setCep(e.target.value)}
                        maxLength={9}
                    />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Logradouro</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Logradouro" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={logradouro}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Número</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Número" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={numero}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Complemento</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Complemento" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={complem}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Bairro</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Bairro" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={bairro}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Cidade</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Cidade" 
                            style={{backgroundColor:"#D8D8D8"}}
                            value={cidade}
                        />
                    </Col>
                    <Col>
                        <Form.Label>UF</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="UF" 
                            maxLength={2}
                            style={{backgroundColor:"#D8D8D8", width:"50px", textAlign:"center"}} 
                            value={uf}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col style={{display:"flex", alignItems:'center', justifyContent:"space-between"}}>
                            <p>{error}</p>
                            <ButtonEntrar 
                                name="Cadastrar!"
                                action={handleRegister}
                            />                   
                    </Col>
                </Row>
            </Form>
        </Container>
        </>
    )
}
