import React, { useEffect, useState } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";


import { Link } from "react-router-dom";

import { FaRegCheckCircle, FaRedoAlt } from "react-icons/fa";


import ModalPizzas from "../../components/modal";
import NavLateral from "../../components/navLateral";
import logoPizzaria from '../../assets/pizzaria_giuseppe.png'
import CardPedido from "../../components/cards/cardPedido";

import useAuth from "../../hooks/useAuth";

import './pedidosUserStyle.css'

export default function PedidosUser(){
    const { listaPedidos } = useAuth();
    const [novosDados, setNovosDados] = useState(listaPedidos)


    const tempo = Date.now()
    const hoje = new Date(tempo)

    const carregaPedidos = async () => {
        const novosPedidos = await listaPedidos;
        setNovosDados(novosPedidos || []);
    }

    useEffect(() => {
        carregaPedidos()
    }, [])

    const recarregaPedidos = () =>{
        carregaPedidos()
        console.log("Oi")
    };
    
    return(
        <Container fluid className="containerPedidosUser">
            <Row className="containerHeaderPedidos">
                <Col>
                    <NavLateral/>
                </Col>
                <Col className="containerLogoUser">
                    <Link to="/infoUser">
                        <img
                            className="d-block w-20"
                            src={logoPizzaria}
                            alt="Pizza Calabresa"
                            height="50px"
                        />
                    </Link>
                </Col>
            </Row>
            <Container fluid className="containerConteudoPedidoUser">
                <Row className="tituloPage">
                    <h1>Faça seu pedido do dia</h1>
                    <p>Aqui você fará seu pedido do dia {hoje.toLocaleDateString()}</p>
                </Row>
                <Row className="rowBotaoModal">
                    <ModalPizzas />
                    
                </Row>
                <Row className="rowCards">
                      {novosDados.length > 0 ? (
                        novosDados.map((data) => (
                            <CardPedido 
                                key={data.id}
                                qnt={data.qntPizzas}
                                sabor1={data.sabor[0]}
                                sabor2={data.sabor[1]}
                                sabor3={data.sabor[2]}
                                preco="R$2"
                            />
                        ))
                      ) : (
                            <p>Sem Pedidos</p>
                        )}
                    
                </Row>
                <Row className="rowBotaoFinalizar">
                    <Button bsPrefix="botaoFinalizarPedidos" onClick={recarregaPedidos} style={{width:"150px", height:"50px"}}> 
                        Finalizar
                        <FaRegCheckCircle size={20}/>
                    </Button>
                </Row>
            </Container>
        </Container>
    )
}
