import React, {useState} from "react";

// Componentes da biblioteca react bootstrap
import {Container, Form, Col, Row} from 'react-bootstrap'

// Componentes da própria aplicação
import logoPizzaria from '../../assets/pizzaria_giuseppe.png'
import ButtonEntrar from "../../components/buttons/buttonEntrar";

// Componentes de outras bibliotecas
import {FaArrowAltCircleLeft} from "react-icons/fa";
import { Link } from "react-router-dom";

// Importando o CSS
import "./registerUserStyle.css"

import api from "../../services/api";

export default function RegisterUser(){
    const [userEmail, setUserEmail] = useState();
    const [userName, setUserName] = useState();
    const [userContact, setUserContact] = useState();
    const [userPassword, setUserPassword] = useState();

    const [error, setError] = useState()

    const register = () => {
        api.post('/clients', {
            name: userName,
            email: userEmail,
            password: userPassword,
            contact: userContact

        }).then((response) => {
            console.log(response)
        });
    }

    return(
        <Container fluid className="containerMainRegister">
            <Row className="containerHeaderRegister">
                <Col>
                    <Link to="/loginUser" >
                        <FaArrowAltCircleLeft size="20px" color="#D30000"/>
                    </Link>
                </Col>
                <Col className="containerLogoRegister">
                    <Link to="/">
                        <img
                            className="d-block w-20"
                            src={logoPizzaria}
                            alt="Pizza Calabresa"
                            height="50px"
                        />
                    </Link>
                </Col>
            </Row>
            <Form className="containerFormRegister">
                <Row >
                    <h1 style={{color: "#D30000", fontWeight:"bold"}}>
                        Cadastro de Cliente
                    </h1>
                    <p>Comece seu cadastro com informações básicas</p>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Nome</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Nome" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                setUserName(e.target.value)
                            }}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Sobrenome</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Sobrenome" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                setUserName(e.target.value)
                            }}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Número para contato</Form.Label>
                        <Form.Control 
                            type="tel" 
                            placeholder="(xx) xxxxx-xxxx" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                [setUserContact(e.target.value), setError("")]
                            }}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Email</Form.Label>
                        <Form.Control 
                       
                            type="email" 
                            placeholder="Email" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                setUserEmail(e.target.value)
                            }}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Form.Label>Senha</Form.Label>
                        <Form.Control 
                            type="password" 
                            placeholder="Senha" 
                            style={{backgroundColor:"#D8D8D8"}}
                            onChange={(e) => {
                                setUserPassword(e.target.value)
                            }}
                        />
                    </Col>
                    <Col>
                        <Form.Label>Confirmar Senha</Form.Label>
                        <Form.Control type="password" placeholder="Confirmar Senha" style={{backgroundColor:"#D8D8D8"}}/>
                    </Col>
                </Row>
                <Row>
                    <Col style={{display:"flex", justifyContent:"flex-end"}}>
                        {/* <Link to="/registerAdress" style={{textDecoration:"none"}}> */}
                            <ButtonEntrar 
                                name="Próximo"
                                action={register}
                            />
                        {/* </Link> */}
                        
                    </Col>
                </Row>
            </Form>
        </Container>

    )
}
