import React, { useEffect, useState } from "react";
import CardPizza from "../cards/cardCardapio";
import Container  from "react-bootstrap/Container";
import Row  from "react-bootstrap/Row";
import Col from "react-bootstrap/Col"

import pizzaCalabresa from '../../assets/pizzaCalabresa.jpg';
import pizzaQueijo from '../../assets/pizzaQueijo.jpg';
import pizzaDoce from '../../assets/pizzaBanana.jpg';

import api from "../../services/api";

import './cardapioStyle.css'





export default function Recipe(){
   const pizzas = useState([]);

   useEffect(() => {
      api.get("/pizzas")
      .then((response) => {
         console.log(response)
      }).catch((err) => {
         if (err.response) {
            // A requisição foi feita e o servidor respondeu com um código de status
            // que sai do alcance de 2xx
            console.error(error.response.data);
            console.error(error.response.status);
            console.error(error.response.headers);
      }
      })
   })

    return(
        <Container fluid className="containerCardapio">
            <Row style={{alignItems:"center", justifyContent:"center", paddingTop:"20px"}}>
               <Col style={{margin:"0px"}}>
