import { createContext, useState } from "react";

import api from "../services/api";

export const AuthContext = createContext({});

export const AuthProvider = ({ children }) => {
    const [client, setClient] = useState();
    const [userNow, setUserNow] = useState();

    const register = (userName, userEmail, userPassword, userContact, userCep, userLogradouro, userNumero, userComplemento, userBairro, userCity, userUf) => {
        
        api.post('/clients', {
            name: userName,
            email: userEmail,
            password: userPassword,
            contact: userContact,
            cep: userCep,
            logradouro: userLogradouro,
            number: userNumber,
            complemento: userComplemento,
            bairro: userBairro,
            city: userCity,
            uf: userUf
        }).then((response) => {
            console.log(response.data)
            setUserNow(userEmail)
        }).catch((error) => {
            console.error(error.response)
        })

    }

    const registerAdress = (userCep, userLogradouro, userNumber, userComplemento, userBairro, userCity, userUf) => {
        api.patch(`/clients`, {
            cep: userCep,
            logradouro: userLogradouro,
            number: userNumber,
            complemento: userComplemento,
            bairro: userBairro,
            city: userCity,
            uf: userUf
        }).then((response) => {
            console.log(response.data)
        }).catch((error) => {
            console.error(error.response)
        })
    }

    const login = (userEmail, userPassword) => {  
        api.get('/clients')
        .then(({ data }) => {
            data.map((item) => {    
                if(item.email === userEmail && item.password === userPassword){
                    setClient({ userEmail, userPassword })
                } else {
                    console.log({message: "Usuário não encontrado."})
                }
                return(
                    console.log("Estou no map")
                )
            })
        })
    }

    const logout = () => {
        setClient(null);
    }
    
    return(
        <AuthContext.Provider value={{client, userNow, loginOK: !!client, login, register, registerAdress, logout}}>
            {children}
        </AuthContext.Provider>
    )

}
