import { createContext, useState } from "react";

import api from "../services/api";

export const AuthContext = createContext({});

export const AuthProvider = ({ children }) => {
    const [client, setClient] = useState()

    const register = (userName, userEmail, userPassword, userContact) => {
        
        api.post('/clients', {
            name: userName,
            email: userEmail,
            password: userPassword,
            contact: userContact
        }).then((response) => {
            console.log(response)
        }).catch((err) => {
            return err
        })

    }

    const login = (userEmail, userPassword) => {  
        api.get('/clients')
        .then(({ data }) => {
            data.map((item) => {    
                if(item.email === userEmail && item.password === userPassword){
                    setClient({ userEmail, userPassword })
                } else {
                    console.log({message: "Usuário não encontrado."})
                }
                return(
                    console.log("Estou no map")
                )
            })
        })
    }

    const logout = () => {
        setClient(null);
    }
    
    return(
        <AuthContext.Provider value={{client, loginOK: !!client, login, register, logout}}>
            {children}
        </AuthContext.Provider>
    )

}
